USE TP_4;
SELECT NOMPATIENT, PRENOMPATIENT FROM PATIENTS;
SELECT NOMPATIENT, PRENOMPATIENT FROM PATIENTS WHERE GRPSANGUIN = 'O+';
SELECT NOMPATIENT, PRENOMPATIENT FROM PATIENTS WHERE GRPSANGUIN = 'AB+' AND POIDS < 100 AND SEXE ='F';
SELECT NOMPATIENT, PRENOMPATIENT FROM PATIENTS WHERE TAILLE = 165;
SELECT DESIGNATION FROM MALADIES WHERE DESIGNATION LIKE 'H%';
SELECT NOMPATIENT, PRENOMPATIENT FROM PATIENTS WHERE NOMPATIENT LIKE "%A%";
SELECT NOMPATIENT , PRENOMPATIENT, POIDS FROM PATIENTS ORDER BY POIDS;
SELECT P.* FROM PATIENTS P INNER JOIN SUIVIS S ON P.CODEPATIENT = S.CODEPATIENT INNER JOIN CONSULTATION C ON S.NUMCONSULTATION = C.NUMCONSULTATION WHERE C.TYPECONSULTATION = "Consultation Générale";
SELECT P.NOMPATIENT, P.PRENOMPATIENT, M.DESIGNATION FROM PATIENTS P INNER JOIN SOUFRIR S ON P.CODEPATIENT = S.CODEPATIENT INNER JOIN MALADIES M ON S.CODEMALADIE = M.CODEMALADIE;
SELECT P.NOMPATIENT, P.PRENOMPATIENT, P.DDN, C.TYPECONSULTATION FROM PATIENTS P INNER JOIN SUIVIS S ON  P.CODEPATIENT = S.CODEPATIENT INNER JOIN CONSULTATION C ON S.NUMCONSULTATION = C.NUMCONSULTATION WHERE YEAR(P.DDN) < 1990;
SELECT P.NOMPATIENT, P.PRENOMPATIENT FROM PATIENTS P INNER JOIN SUIVIS S ON P.CODEPATIENT = S.CODEPATIENT INNER JOIN CONSULTATION C ON S.NUMCONSULTATION = C.NUMCONSULTATION WHERE C.TYPECONSULTATION = "Cholestérol Checkup";
SELECT P.NOMPATIENT, P.PRENOMPATIENT FROM PATIENTS P INNER JOIN SOUFRIR S ON P.CODEPATIENT = S.CODEPATIENT INNER JOIN MALADIES M ON S.CODEMALADIE = M.CODEMALADIE WHERE M.DESIGNATION = "Grippe";
SELECT GRPSANGUIN, COUNT(GRPSANGUIN) AS NUMBERS FROM PATIENTS GROUP BY GRPSANGUIN;
SELECT C.TYPECONSULTATION, COUNT(C.TYPECONSULTATION) AS NUMBERS FROM PATIENTS P INNER JOIN SUIVIS S ON P.CODEPATIENT = S.CODEPATIENT INNER JOIN CONSULTATION C ON S.NUMCONSULTATION = C.NUMCONSULTATION GROUP BY C.TYPECONSULTATION;